mapper.insert:
        insert into tb_file_category (file_category_name, file_category_format, create_time)
        VALUES (
                   #{fileCategoryName}, #{fileCategoryFormat}, #{createTime}
               )

mapper.select:
select file_category_id, file_category_name, file_category_format, create_time
from tb_file_category
where file_category_id = #{fileCategoryId};
